import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CurrencyConverter = () => {
    const initialCurrencies = ['USD', 'EUR', 'RUB', 'BYN'];

    const [currencies, setCurrencies] = useState({});
    const [values, setValues] = useState({
        USD: '1',
        EUR: '',
        RUB: '',
        BYN: ''
    });
    const [availableCurrencies, setAvailableCurrencies] = useState([]);
    const [selectedCurrency, setSelectedCurrency] = useState('');

    useEffect(() => {
        const fetchInitialCurrencies = async () => {
            try {
                const requiredCurrencies = initialCurrencies.join(',');
                const response = await axios.get(`http://localhost:8000/api/currencies/convert?currencies=${requiredCurrencies}`);
                const currenciesObj = {};
                response.data.forEach(currency => {
                    currenciesObj[currency.Cur_Abbreviation] = currency.valueToUSD;
                });
                setCurrencies(currenciesObj);

                // Set initial USD value and convert it
                handleInputChange('USD', '1');
            } catch (error) {
                console.error("Error fetching currencies:", error);
            }
        };

        const fetchAllCurrencies = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/currencies');
                setAvailableCurrencies(response.data.map(currency => currency.Cur_Abbreviation));
            } catch (error) {
                console.error("Error fetching all currencies:", error);
            }
        };

        fetchInitialCurrencies();
        fetchAllCurrencies();
    }, []);

    const handleInputChange = async (currency, inputValue) => {
        if (inputValue === '' || inputValue === undefined) {
            setValues(prevValues => ({ ...prevValues, [currency]: '' }));
            return;
        }

        try {
            const response = await axios.post(`http://localhost:8000/api/currencies/convert/${currency}`, { value: inputValue });
            setValues({
                ...values,
                ...response.data
            });
        } catch (error) {
            console.error("Error converting currency:", error);
        }
    };

    const addCurrency = () => {
        if (selectedCurrency) {
            setValues(prevValues => ({ ...prevValues, [selectedCurrency]: '' }));
        }
    };

    return (
        <div>
            <h2>Currency Converter</h2>
            {Object.keys(currencies).length > 0 ? (
                <div>
                    {Object.keys(values).map(currency => (
                        <div key={currency}>
                            <label>{currency}:</label>
                            <input
                                type="number"
                                value={values[currency]}
                                onChange={e => handleInputChange(currency, e.target.value)}
                            />
                        </div>
                    ))}
                    <div>
                        <select value={selectedCurrency} onChange={e => setSelectedCurrency(e.target.value)}>
                            <option value="">Select currency</option>
                            {availableCurrencies.filter(currency => !Object.keys(values).includes(currency)).map(currency => (
                                <option key={currency} value={currency}>
                                    {currency}
                                </option>
                            ))}
                        </select>
                        <button onClick={addCurrency}>Add Currency</button>
                    </div>
                </div>
            ) : (
                <p>Loading currencies...</p>
            )}
        </div>
    );
}

export default CurrencyConverter;
