import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [rates, setRates] = useState({});

    useEffect(() => {
        // Загрузка списка курсов валют
        fetch('https://api.nbrb.by/exrates/rates?periodicity=0')
            .then(response => response.json())
            .then(data => {
                const currencyMap = {
                    USD: 1, // Так как нам нужен курс к US доллару, курс USD к USD всегда равен 1
                    EUR: null,
                    RUB: null,
                    BYN: null
                };
                data.forEach(item => {
                    if (currencyMap[item.Cur_Abbreviation] !== undefined) {
                        currencyMap[item.Cur_Abbreviation] = item.Cur_OfficialRate / (item.Cur_Scale / item.Cur_OfficialRate);
                    }
                });
                // Конвертируем курсы валют в курс к USD
                currencyMap.EUR = currencyMap.EUR / currencyMap.USD;
                currencyMap.RUB = currencyMap.RUB / currencyMap.USD;
                currencyMap.BYN = 1 / currencyMap.USD;
                currencyMap.USD = 1; // Устанавливаем курс USD к USD обратно в 1

                setRates(currencyMap);
            });
    }, []);

    return (
        <div className="App">
            <h1>Курсы валют к USD</h1>
            {Object.entries(rates).map(([currency, rate]) => (
                <div key={currency}>
                    {currency}: {rate}
                </div>
            ))}
        </div>
    );
}

export default App;
