import React from 'react';

function CurrencyInput({ abbreviation, value, onChange }) {
    return (
        <div>
            <label>
                {abbreviation}:
                <input
                    type="number"
                    value={value || ''}
                    onChange={e => onChange(e, abbreviation)}
                />
            </label>
        </div>
    );
}

export default CurrencyInput;
