// src/CurrencyList.js
import React, { useState, useEffect } from 'react';

function CurrencyConverter() {
    const [currencies, setCurrencies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('https://api.nbrb.by/exrates/rates?periodicity=0')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then(data => {
                const relevantCurrencies = data.filter(item =>
                    ['USD', 'EUR', 'RUB', 'BYN'].includes(item.Cur_Abbreviation)
                );
                setCurrencies(relevantCurrencies);
                setIsLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setIsLoading(false);
            });
    }, []);

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <ul>
            {currencies.map(currency => (
                <li key={currency.Cur_ID}>
                    {currency.Cur_Abbreviation}: {currency.Cur_OfficialRate}
                </li>
            ))}
        </ul>
    );
}

export default CurrencyConverter;
