import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const CurrencyConverter = () => {
    const initialCurrencies = ['USD', 'EUR', 'RUB', 'BYN'];

    const [values, setValues] = useState({
        USD: '1',
        EUR: '',
        RUB: '',
        BYN: ''
    });
    const [availableCurrencies, setAvailableCurrencies] = useState(initialCurrencies);
    const [allCurrencies, setAllCurrencies] = useState([]);
    const [selectedCurrency, setSelectedCurrency] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const allCurrenciesResponse = await axios.get('http://localhost:8000/api/currencies');
                setAllCurrencies(allCurrenciesResponse.data.map(currency => currency.Cur_Abbreviation));

                const initialConversionsResponse = await axios.post(`http://localhost:8000/api/currencies/convert/USD`, { value: '1' });
                setValues(prevValues => ({
                    ...prevValues,
                    ...initialConversionsResponse.data
                }));
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    const handleInputChange = useCallback(async (currency, inputValue) => {
        if (!/^[0-9.,]*$/.test(inputValue)) {
            return;
        }

        if (inputValue === '' || inputValue === undefined) {
            setValues(prevValues => ({ ...prevValues, [currency]: '' }));
            return;
        }

        try {
            // Конвертировать введенное значение в базовую валюту (например, USD)
            const baseCurrencyResponse = await axios.post(`http://localhost:8000/api/currencies/convert/${currency}`, { value: inputValue, toCurrency: 'USD' });
            const baseValue = baseCurrencyResponse.data.USD;

            // Теперь конвертируйте базовое значение во все доступные валюты
            const allConversionsResponse = await axios.post(`http://localhost:8000/api/currencies/convert/USD`, { value: baseValue });
            setValues(prevValues => ({
                ...prevValues,
                ...allConversionsResponse.data
            }));
        } catch (error) {
            console.error("Error converting currency:", error);
        }
    }, []);

    const debouncedHandleInputChange = useDebounce(handleInputChange, 300);

    const addCurrency = async () => {
        if (selectedCurrency && !availableCurrencies.includes(selectedCurrency)) {
            setAvailableCurrencies(prevCurrencies => [...prevCurrencies, selectedCurrency]);
            setValues(prevValues => ({ ...prevValues, [selectedCurrency]: '' }));

            try {
                const response = await axios.post(`http://localhost:8000/api/currencies/convert/USD`, { value: values.USD });
                setValues(prevValues => ({
                    ...prevValues,
                    [selectedCurrency]: response.data[selectedCurrency]
                }));
            } catch (error) {
                console.error("Error converting for new currency:", error);
            }
        }
    };

    const removeCurrency = (currencyToRemove) => {
        setAvailableCurrencies(prevCurrencies => prevCurrencies.filter(currency => currency !== currencyToRemove));
        setValues(prevValues => {
            const updatedValues = { ...prevValues };
            delete updatedValues[currencyToRemove];
            return updatedValues;
        });
    };

    return (
        <div>
            <h2>Currency Converter</h2>
            <div>
                {availableCurrencies.map(currency => (
                    <div key={currency} style={{ marginBottom: "10px" }}>
                        <label>{currency}:</label>
                        <input
                            type="text"  // Поменял на text, чтобы можно было использовать pattern
                            pattern="^\d*\.?\d*$"
                            value={values[currency] || ''}
                            onChange={e => handleInputChange(currency, e.target.value)}
                        />
                        {!initialCurrencies.includes(currency) && (
                            <button onClick={() => removeCurrency(currency)}>Remove</button>
                        )}
                    </div>
                ))}
                <div>
                    <select value={selectedCurrency} onChange={e => setSelectedCurrency(e.target.value)}>
                        <option value="">Select currency</option>
                        {allCurrencies.filter(currency => !availableCurrencies.includes(currency)).map(currency => (
                            <option key={currency} value={currency}>
                                {currency}
                            </option>
                        ))}
                    </select>
                    <button onClick={addCurrency}>Add Currency</button>
                </div>
            </div>
        </div>
    );
}

export default CurrencyConverter;
