import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CurrencyConverter = () => {
    const [currencies, setCurrencies] = useState({});
    const [values, setValues] = useState({
        USD: '',
        EUR: '',
        RUB: '',
        BYN: ''
    });

    useEffect(() => {
        const fetchInitialCurrencies = async () => {
            try {
                const requiredCurrencies = ['USD', 'EUR', 'RUB', 'BYN'].join(',');
                const response = await axios.get(`http://localhost:8000/api/currencies/convert?currencies=${requiredCurrencies}`);
                const currenciesObj = {};
                response.data.forEach(currency => {
                    currenciesObj[currency.Cur_Abbreviation] = currency.valueToUSD;
                });
                setCurrencies(currenciesObj);

                // Set initial USD value and convert it
                handleInputChange('USD', '1');
            } catch (error) {
                console.error("Error fetching currencies:", error);
            }
        };

        fetchInitialCurrencies();
    }, []);

    const handleInputChange = async (currencyType, value) => {
        setValues(prevValues => ({ ...prevValues, [currencyType]: value }));

        try {
            if (value === '') {
                value = '0'; // Если инпут пустой, присваиваем значение 0 для корректной конвертации
            }

            const response = await axios.post('http://localhost:8000/api/currencies/convert', {
                value: parseFloat(value),
                fromCurrency: currencyType
            });

            const updatedValues = { ...response.data };
            if (value === '0') { // Если исходное значение было пустым, очищаем все инпуты
                for (const key in updatedValues) {
                    updatedValues[key] = '';
                }
            }
            setValues(updatedValues);
        } catch (error) {
            console.error("Error converting currency:", error);
        }
    };


    return (
        <div>
            <h2>Currency Converter</h2>
            {Object.keys(currencies).length > 0 ? (
                <div>
                    {['USD', 'EUR', 'RUB', 'BYN'].map(currency => (
                        <div key={currency}>
                            <label>{currency}:</label>
                            <input
                                type="number"
                                value={values[currency]}
                                onChange={e => handleInputChange(currency, e.target.value)}
                            />
                        </div>
                    ))}
                </div>
            ) : (
                <p>Loading currencies...</p>
            )}
        </div>
    );
}

export default CurrencyConverter;
