import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CurrencyTable = () => {
    const [currencies, setCurrencies] = useState([]);
    const [sortType, setSortType] = useState('byName'); // byName или byValueToUSD

    // Функция для получения отсортированного списка валют
    const fetchSortedCurrencies = async (type) => {
        try {
            const endpoint = `/currencies/sort/${type === 'byName' ? 'byName' : 'byValueToUSD'}`;
            const response = await axios.get(endpoint);
            setCurrencies(response.data);
        } catch (error) {
            console.error("Error fetching sorted currencies:", error);
        }
    };

    useEffect(() => {
        fetchSortedCurrencies(sortType);
    }, [sortType]);

    return (
        <div>
            <button onClick={() => setSortType('byName')}>Sort by Name</button>
            <button onClick={() => setSortType('byValueToUSD')}>Sort by Value to USD</button>

            <table>
                <thead>
                <tr>
                    <th>Name</th>
                    <th>Value to USD</th>
                </tr>
                </thead>
                <tbody>
                {currencies.map(currency => (
                    <tr key={currency.Cur_ID}>
                        <td>{currency.Cur_Name}</td>
                        <td>{currency.valueToUSD}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default CurrencyTable;
