import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CurrencyTable() {
    const [currencies, setCurrencies] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchCurrencies() {
            try {
                const response = await axios.get('http://localhost:6000/currencies');
                setCurrencies(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching currencies:", error);
                setLoading(false);
            }
        }

        fetchCurrencies();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <table>
            <thead>
            <tr>
                <th>Currency Name</th>
                <th>Rate to 1 USD</th>
            </tr>
            </thead>
            <tbody>
            {currencies.map(currency => (
                <tr key={currency.Cur_ID}>
                    <td>{currency.Cur_Name}</td>
                    <td>{currency.Cur_OfficialRate}</td>
                    {/* Заметка: Полагаю, что Cur_OfficialRate - это поле с курсом валюты к 1 доллару. Если нет, замените на правильное поле. */}
                </tr>
            ))}
            </tbody>
        </table>
    );
}

export default CurrencyTable;
