import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CurrencyInput from './components/CurrencyInput';

function App() {
    const [usdRate, setUsdRate] = useState("");
    const [eurRate, setEurRate] = useState("");
    const [rubRate, setRubRate] = useState("");
    const [bynRate, setBynRate] = useState("");

    useEffect(() => {
        async function fetchCurrencies() {
            try {
                const response = await axios.get('http://localhost:3000/currencies');
                const rates = response.data;
                console.log(rates)

                setUsdRate(rates.USD.toFixed(2));
                setEurRate(rates.EUR.toFixed(2));
                setRubRate(rates.RUB.toFixed(2));
                setBynRate(rates.BYN.toFixed(2));
            } catch (error) {
                console.error("Ошибка при получении данных:", error);
            }
        }

        fetchCurrencies();
    }, []);

    return (
        <div className="App">
            <h1>Курсы валют к доллару:</h1>
            <CurrencyInput label="USD" value={usdRate} onChange={(e) => setUsdRate(e.target.value)} />
            <CurrencyInput label="EUR" value={eurRate} onChange={(e) => setEurRate(e.target.value)} />
            <CurrencyInput label="RUB" value={rubRate} onChange={(e) => setRubRate(e.target.value)} />
            <CurrencyInput label="BYN (равно курсу USD)" value={bynRate} onChange={(e) => setBynRate(e.target.value)} />
        </div>
    );
}

export default App;
