import React, { useState, useEffect } from 'react';

function CurrencyList() {
    const [currencies, setCurrencies] = useState([]);
    const [values, setValues] = useState({ USD: 1 }); // Инициализация инпута для доллара значением 1
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('https://api.nbrb.by/exrates/rates?periodicity=0')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then(data => {
                const relevantCurrencies = data.filter(item =>
                    ['USD', 'EUR', 'RUB', 'BYN'].includes(item.Cur_Abbreviation)
                );
                setCurrencies(relevantCurrencies);
                setIsLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setIsLoading(false);
            });
    }, []);

    useEffect(() => {
        handleInputChange({ target: { value: 1 } }, 'USD');
    }, [currencies]); // Пересчет при инициализации значений

    function handleInputChange(e, abbreviation) {
        const newValue = parseFloat(e.target.value || 0);
        const updatedValues = {};

        for (const currency of currencies) {
            if (currency.Cur_Abbreviation === 'USD') {
                updatedValues[currency.Cur_Abbreviation] = abbreviation === 'USD'
                    ? newValue
                    : newValue * currencies.find(cur => cur.Cur_Abbreviation === abbreviation).Cur_OfficialRate;
            } else {
                const relativeRate = currency.Cur_OfficialRate / currencies.find(cur => cur.Cur_Abbreviation === 'USD').Cur_OfficialRate;
                updatedValues[currency.Cur_Abbreviation] = abbreviation === 'USD'
                    ? newValue * relativeRate
                    : newValue * relativeRate / currencies.find(cur => cur.Cur_Abbreviation === abbreviation).Cur_OfficialRate;
            }
        }

        setValues(updatedValues);
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            {currencies.map(currency => (
                <div key={currency.Cur_ID}>
                    <label>
                        {currency.Cur_Abbreviation}:
                        <input
                            type="number"
                            value={values[currency.Cur_Abbreviation] || ''}
                            onChange={e => handleInputChange(e, currency.Cur_Abbreviation)}
                        />
                    </label>
                </div>
            ))}
            <p>1 USD = {values.BYN || 0} BYN</p> {/* Отображение курса белорусского рубля к доллару */}
        </div>
    );
}

export default CurrencyList;
