import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ConverterPage = () => {
    const [rates, setRates] = useState({});
    const [inputValues, setInputValues] = useState({
        USD: 0,
        EUR: 0,
        RUB: 0,
        BYN: 0,
    });

    useEffect(() => {
        const fetchRates = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/currencies');
                const fetchedRates = {};
                response.data.forEach(rate => {
                    if (['USD', 'EUR', 'RUB', 'BYN'].includes(rate.Cur_Abbreviation)) {
                        fetchedRates[rate.Cur_Abbreviation] = rate.valueToUSD;
                    }
                });
                setRates(fetchedRates);
            } catch (error) {
                console.error("Error fetching currencies:", error);
            }
        };

        fetchRates();
    }, []);

    const handleInputChange = (currency, value) => {
        const newInputValues = {
            USD: value * (1 / rates.USD),
            EUR: value * (1 / rates.EUR),
            RUB: value * (1 / rates.RUB),
            BYN: value * (1 / rates.BYN),
        };
        setInputValues(newInputValues);
    };

    return (
        <div>
            <h1>Currency Calculator</h1>
            {['USD', 'EUR', 'RUB', 'BYN'].map(currency => (
                <div key={currency}>
                    <label>{currency}:
                        <input
                            type="number"
                            value={inputValues[currency]}
                            onChange={e => handleInputChange(currency, e.target.value)}
                        />
                    </label>
                </div>
            ))}
        </div>
    );
}

export default ConverterPage;
