import React, { useState, useEffect } from 'react';
import CurrencyInput from '../components/CurrencyInput';

function CurrencyList() {
    const [currencies, setCurrencies] = useState([]);
    const [values, setValues] = useState({ USD: 1 });
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('https://api.nbrb.by/exrates/rates?periodicity=0')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then(data => {
                const usdCurrency = {
                    Cur_Abbreviation: 'USD',
                    Cur_OfficialRate: 1
                };
                const relevantCurrencies = data.filter(item =>
                    ['USD', 'EUR', 'RUB', 'BYN'].includes(item.Cur_Abbreviation)
                );
                relevantCurrencies.push(usdCurrency);
                setCurrencies(relevantCurrencies);
                setIsLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setIsLoading(false);
            });
    }, []);

    useEffect(() => {
        handleInputChange({ target: { value: 1 } }, 'USD');
    }, [currencies]);

    function handleInputChange(e, abbreviation) {
        const newValue = parseFloat(e.target.value || 0);
        const usdRate = currencies.find(cur => cur.Cur_Abbreviation === 'USD').Cur_OfficialRate;
        const updatedValues = { ...values };

        if (abbreviation === 'USD') {
            for (const currency of currencies) {
                const currentRate = currency.Cur_OfficialRate;
                updatedValues[currency.Cur_Abbreviation] = newValue * (currentRate / usdRate);
            }
        } else {
            const targetRate = currencies.find(cur => cur.Cur_Abbreviation === abbreviation).Cur_OfficialRate;
            const usdValue = abbreviation === 'BYN' ? newValue / usdRate : newValue * usdRate / targetRate;

            for (const currency of currencies) {
                const currentRate = currency.Cur_OfficialRate;
                updatedValues[currency.Cur_Abbreviation] = usdValue * (currentRate / usdRate);
            }
        }

        setValues(updatedValues);
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            {currencies.map(currency => (
                <CurrencyInput
                    key={currency.Cur_Abbreviation}
                    abbreviation={currency.Cur_Abbreviation}
                    value={values[currency.Cur_Abbreviation]}
                    onChange={handleInputChange}
                />
            ))}
        </div>
    );
}

export default CurrencyList;
