function useDebounce(fn, delay) {
    const [timeoutId, setTimeoutId] = useState(null);

    return (...args) => {
        if (timeoutId) {
            clearTimeout(timeoutId);
        }

        const newTimeoutId = setTimeout(() => {
            fn(...args);
        }, delay);

        setTimeoutId(newTimeoutId);
    };
}

export {useDebounce};
