import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ConverterPage() {
    const [currencies, setCurrencies] = useState([]);
    const [values, setValues] = useState({
        USD: 1,
        EUR: 0,
        RUB: 0,
        BYN: 0
    });
    const [selectedCurrency, setSelectedCurrency] = useState("");

    useEffect(() => {
        async function fetchCurrencies() {
            try {
                const response = await axios.get('http://localhost:8000/api/currencies');
                setCurrencies(response.data);
            } catch (error) {
                console.error('Failed to fetch rates', error);
            }
        }
        fetchCurrencies();
    }, []);

    const handleInputChange = (currency, value) => {
        const newValues = { ...values };

        for (let cur in values) {
            if (cur === currency) continue;
            newValues[cur] = (currencies[cur] / currencies[currency]) * value;
        }

        setValues(newValues);
    };

    const addCurrency = () => {
        if (selectedCurrency && !values[selectedCurrency]) {
            setValues(prevValues => ({ ...prevValues, [selectedCurrency]: 0 }));
        }
    };

    return (
        <div className="App">
            <h1>Currency Calculator</h1>

            {Object.entries(values).map(([currency, value]) => (
                <div key={currency}>
                    <label>{currency}: </label>
                    <input
                        type="number"
                        value={value}
                        onChange={(e) => handleInputChange(currency, e.target.value)}
                    />
                </div>
            ))}

            <div>
                <select value={selectedCurrency} onChange={e => setSelectedCurrency(e.target.value)}>
                    <option value="">-- Add Currency --</option>
                    {currencies.map(cur => (
                        !values[cur] ? <option key={cur} value={cur}>{cur}</option> : null
                    ))}
                </select>
                <button onClick={addCurrency}>Add</button>
            </div>
        </div>
    );
}

export default ConverterPage;
