import React, { useEffect } from 'react';
import { useCurrency } from './CurrencyContext';
import axios from 'axios';

const CurrencyTable = () => {
    const { currenciesData, setCurrenciesData } = useCurrency();
    const [sortType, setSortType] = useState('byName');

    const fetchSortedCurrencies = async (type) => {
        try {
            const response = await axios.get(`http://localhost:8000/api/currencies/sort/${type === 'byName' ? 'byName' : 'byValueToUSD'}`);
            setCurrenciesData(response.data);
        } catch (error) {
            console.error("Error fetching sorted currencies:", error);
        }
    };

    useEffect(() => {
        if (!currenciesData.length) {
            // Запрашиваем данные только если они ещё не были получены
            fetchSortedCurrencies(sortType);
        }
    }, [sortType]);

    return (
        <div>
            <label>Сортировать по:
                <select value={sortType} onChange={e => setSortType(e.target.value)}>
                    <option value="byName">Имя</option>
                    <option value="byValueToUSD">Значение к USD</option>
                </select>
            </label>

            <table>
                <thead>
                <tr>
                    <th>Имя</th>
                    <th>Значение к USD</th>
                </tr>
                </thead>
                <tbody>
                {currenciesData.map(currency => (
                    <tr key={currency.Cur_ID}>
                        <td>{currency.Cur_Scale} {currency.Cur_Name}</td>
                        <td>{currency.valueToUSD}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default CurrencyTable;
