import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [rates, setRates] = useState({});

    useEffect(() => {
        // Запросы
        const usdRateRequest = fetch('https://api.nbrb.by/exrates/rates/USD?parammode=2');
        const allRatesRequest = fetch('https://api.nbrb.by/exrates/rates?periodicity=0');

        Promise.all([usdRateRequest, allRatesRequest])
            .then(responses => Promise.all(responses.map(response => response.json())))
            .then(([usdData, allRatesData]) => {
                const usdToBynRate = usdData.Cur_OfficialRate / usdData.Cur_Scale;
                const bynToUsdRate = 1 / usdToBynRate;

                const currencyMap = {
                    USD: 1, // Так как нам нужен курс к US доллару, курс USD к USD всегда равен 1
                    EUR: null,
                    RUB: null,
                    BYN: bynToUsdRate
                };

                allRatesData.forEach(item => {
                    if (currencyMap[item.Cur_Abbreviation] !== undefined && item.Cur_Abbreviation !== "USD") {
                        currencyMap[item.Cur_Abbreviation] = (item.Cur_OfficialRate / item.Cur_Scale) / bynToUsdRate;
                    }
                });

                setRates(currencyMap);
            })
            .catch(error => {
                console.error("Ошибка при получении данных:", error);
            });
    }, []);


    return (
        <div className="App">
            <h1>Курсы валют к USD</h1>
            {Object.entries(rates).map(([currency, rate]) => (
                <div key={currency}>
                    {currency}: {rate}
                </div>
            ))}
        </div>
    );
}

export default App;
