// src/CurrencyList.js
import React, { useState, useEffect } from 'react';

function CurrencyList() {
    const [currencies, setCurrencies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('https://api.nbrb.by/exrates/rates?periodicity=0')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then(data => {
                const relevantCurrencies = data.filter(item =>
                    ['USD', 'EUR', 'RUB', 'BYN'].includes(item.Cur_Abbreviation)
                );
                setCurrencies(relevantCurrencies);
                setIsLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setIsLoading(false);
            });
    }, []);

    function getBYNRate() {
        const usd = currencies.find(currency => currency.Cur_Abbreviation === 'USD');
        if (usd) {
            return (1 / usd.Cur_OfficialRate).toFixed(4); // Округляем до 4 знаков после запятой
        }
        return null;
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <ul>
                {currencies.map(currency => (
                    <li key={currency.Cur_ID}>
                        {currency.Cur_Abbreviation}: {currency.Cur_OfficialRate}
                    </li>
                ))}
            </ul>
            <p>BYN относительно USD: {getBYNRate()}</p>
        </div>
    );
}

export default CurrencyList;
