import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CurrencyInput from './components/CurrencyInput';

function App() {
    const [usdRate, setUsdRate] = useState("");
    const [eurRate, setEurRate] = useState("");
    const [rubRate, setRubRate] = useState("");
    const [bynRate, setBynRate] = useState("");

    useEffect(() => {
        async function fetchCurrencies() {
            try {
                const response = await axios.get('http://localhost:3000/currencies');
                const rates = response.data;

                setUsdRate(rates.USD.toFixed(2));
                setEurRate(rates.EUR.toFixed(2));
                setRubRate(rates.RUB.toFixed(2));
                setBynRate(rates.BYN.toFixed(2));
            } catch (error) {
                console.error("Ошибка при получении данных:", error);
            }
        }

        fetchCurrencies();
    }, []);

    const handleRateChange = async (currency, rate) => {
        try {
            const response = await axios.post('http://localhost:8000/update-currency', { currency, rate });
            const updatedRates = response.data;

            setUsdRate(updatedRates.USD.toFixed(2));
            setEurRate(updatedRates.EUR.toFixed(2));
            setRubRate(updatedRates.RUB.toFixed(2));
            setBynRate(updatedRates.BYN.toFixed(2));
        } catch (error) {
            console.error("Ошибка при обновлении курса валюты:", error);
        }
    };

    return (
        <div className="App">
            <h1>Курсы валют к доллару:</h1>
            <CurrencyInput
                label="USD"
                value={usdRate}
                onChange={(e) => {
                    setUsdRate(e.target.value);
                    handleRateChange("USD", e.target.value);
                }}
            />
            <CurrencyInput
                label="EUR"
                value={eurRate}
                onChange={(e) => {
                    setEurRate(e.target.value);
                    handleRateChange("EUR", e.target.value);
                }}
            />
            <CurrencyInput
                label="RUB"
                value={rubRate}
                onChange={(e) => {
                    setRubRate(e.target.value);
                    handleRateChange("RUB", e.target.value);
                }}
            />
            <CurrencyInput
                label="BYN (равно курсу USD)"
                value={bynRate}
                onChange={(e) => {
                    setBynRate(e.target.value);
                    handleRateChange("BYN", e.target.value);
                }}
            />
        </div>
    );
}

export default App;
