// src/CurrencyList.js
import React, { useState, useEffect } from 'react';

function CurrencyList() {
    const [currencies, setCurrencies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('https://api.nbrb.by/exrates/rates?periodicity=0')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then(data => {
                const relevantCurrencies = data.filter(item =>
                    ['USD', 'EUR', 'RUB', 'BYN'].includes(item.Cur_Abbreviation)
                );
                setCurrencies(relevantCurrencies);
                setIsLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setIsLoading(false);
            });
    }, []);

    function getRateRelativeToUSD(curRate) {
        const usdRate = currencies.find(currency => currency.Cur_Abbreviation === 'USD').Cur_OfficialRate;
        return (curRate / usdRate).toFixed(4);  // Округляем до 4 знаков после запятой
    }

    function getBYNtoUSDRate() {
        const usdRate = currencies.find(currency => currency.Cur_Abbreviation === 'USD').Cur_OfficialRate;
        return (1 / usdRate).toFixed(4);  // Округляем до 4 знаков после запятой
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <ul>
                {currencies.map(currency => (
                    <li key={currency.Cur_ID}>
                        {currency.Cur_Abbreviation} к USD:
                        {currency.Cur_Abbreviation === 'USD'
                            ? '1'
                            : getRateRelativeToUSD(currency.Cur_OfficialRate)}
                    </li>
                ))}
            </ul>
            <p>BYN к USD: {getBYNtoUSDRate()}</p>
        </div>
    );
}

export default CurrencyList;
